import { Button, TextField } from "@mui/material";
import React, { useState, useEffect } from "react";
import axios from "axios";

const ChannelsPage = ({ user, ws }) => {
  const [DatasList, setDatasList] = useState([]);
  const [message, setMessage] = useState("");

  const [sub, setSub] = useState([]);

  const getSub = async () => {
    const token = localStorage.getItem("token");
    try {
      const response = await axios.get(`http://localhost:8000/api/get_sub`, {
        headers: {
          Authorization: `Token ${token}`,
        },
      });
      setSub(response.data);
    } catch {
      alert("Erreur du serveur...");
    }
  };

  useEffect(() => {
    getSub();
  }, []);

  ws.onopen = () => console.log("ws opened");
  ws.onmessage = (e) => {
    const data = JSON.parse(e.data);
    setDatasList((prevDatasList) => [...prevDatasList, data]);
  };
  ws.onclose = () => console.log("ws closed");
  const SendMessage = () => {
    ws.send(
      JSON.stringify({
        message: message,
        user: user.email,
      })
    );
    setMessage("");
  };

  return (
    <div>
      <h1>Page des chats</h1>
      <div className="roomButtonBox">
      {sub.map((s, index) => (
        <div className="roomButton">
        <Button
          variant="contained"
          margin="normal"
          //onClick={() => SendMessage()}
          color="secondary"
          key={index}
        >
          {s.name}
        </Button>
        </div>
      ))}
      </div>
      <TextField
        id="message"
        label="Enter message"
        size="normal"
        variant="filled"
        margin="normal"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button
        variant="contained"
        margin="normal"
        onClick={() => SendMessage()}
        color="success"
      >
        Send Message
      </Button>
      {DatasList.map((d, index) => (
        <h1 key={index}>
          {d.message},{d.user}
        </h1>
      ))}
    </div>
  );
};

export default ChannelsPage;
