import Form from "react-bootstrap/Form";
import React from "react";
import Button from "react-bootstrap/Button";
import { useNavigate } from "react-router";
import { Card } from "react-bootstrap";
import { motion, useAnimation } from "framer-motion";

const SignUp = () => {
  const navigate = useNavigate();
  const controls = useAnimation();

  const ballVariants = {
    initial: { x: -window.innerWidth / 2, y: -window.innerHeight / 2 }, // Position initiale en haut à gauche
    animate: {
      x: [-window.innerWidth / 2, window.innerWidth / 2, window.innerWidth / 2, -window.innerWidth / 2],
      y: [-window.innerHeight / 2, -window.innerHeight / 2, window.innerHeight / 2, window.innerHeight / 2],
      transition: {
        duration: 5, // Durée de l'animation
        ease: 'linear', // Courbe d'animation linéaire pour des rebonds constants
        repeat: Infinity, // Répète l'animation indéfiniment
      },
    },
  };

  const GoToSignIn = () => {
    navigate("/");
  };

  return (
    <div className="background_signin">
      <motion.div
        variants={ballVariants}
        initial="initial"
        animate="animate"
        onAnimationComplete={() => controls.start("animate")}
      >
        <img
          className="ball"
          src="/images/ballon_football.png" 
          alt="Balle de football"
        />
      </motion.div>
      <motion.div
        initial={{ y: "5vh" }}
        animate={{ y: 0 }}
        transition={{ duration: 1, type: "spring", stiffness: 100 }}
      >
        <Card
          border="success"
          style={{ width: "18rem" }}
          className="card_signin"
        >
          <Card.Header className="cityty_style">Cityty</Card.Header>
          <Card.Body>
            <Form>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control type="email" placeholder="Enter email" />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Control type="password" placeholder="Password" />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Control
                  type="password"
                  placeholder="Confirm your password"
                />
              </Form.Group>
            </Form>
            <div className="container-button_form">
              <Button variant="link" className="button_form">
                Submit
              </Button>
              <Button
                variant="link"
                className="button_form"
                onClick={() => GoToSignIn()}
              >
                Back
              </Button>
            </div>
          </Card.Body>
        </Card>
      </motion.div>
    </div>
  );
};

export default SignUp;
