import { Button, TextField } from "@mui/material";
import React, { useEffect, useState } from "react";

const ChannelsPage = () => {
  const [datas, setDatas] = useState([]);
  const [lastdata,setLastdata] = useState(null);
  const [message, setMessage] = useState("");
  const roomName = "salle"; // Remplacez par le nom de votre salle
  const user = "Thomas";
  //const chatSocket = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

  const ws = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

  ws.onopen = () => console.log("ws opened");

  ws.onclose = () => console.log("ws closed");

  ws.onmessage = function (e) {
    const data = JSON.parse(e.data);
    console.log("Data received from WebSocket:", data);
    setLastdata(data);
  };

  const SendMessage = () => {
    ws.send(
      JSON.stringify({
        message: message,
        user: user,
      })
    );
  };

  useEffect(() => {
    if (lastdata){
      // Vérifiez si lastdata n'est pas null
      if (lastdata.id !== datas[datas.length - 1].id){
      setDatas((prevDatas) => [...prevDatas, lastdata]);
      }
    }
  }, [lastdata]); // Déclenchez l'effet lorsque lastdata change

  return (
    <div>
      <h1>Page des chats</h1>
      <TextField
        id="message"
        label="Enter message"
        size="normal"
        variant="filled"
        margin="normal"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button
        variant="contained"
        margin="normal"
        onClick={() => SendMessage()}
        color="success"
      >
        Send Message
      </Button>
      {datas.map((d,index) => (
        <h1 key={index}>{d.message}</h1>
      ))}
    </div>
  );
};

export default ChannelsPage;
