import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import SignIn from "./SignIn";
import SignUp from "./SignUp";
import { motion } from "framer-motion";

function ConnectPage() {
  const colors = ["#33ff55", "#efaf1a", "#4d72f1"]; // Les couleurs que vous souhaitez utiliser
  const colorIndex = 0;
  const nextColorIndex = (colorIndex + 1) % colors.length;



  return (
    <motion.div
      className="background_signin"
      initial={{ backgroundColor: colors[colorIndex] }}
      animate={{ backgroundColor: colors[nextColorIndex]}}
      transition={{
        duration: 60,
        ease: "linear",
        repeat: Infinity,
        repeatType: "loop",
      }}
    >
      <Router>
        <Routes>
          <Route exact path="/" element={<SignIn />} />
          <Route exact path="/signup" element={<SignUp />} />
        </Routes>
      </Router>
    </motion.div>
  );
}

export default ConnectPage;
