import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MemberPage = () => {
  const [user, setUser] = useState({});

  const UserData = async () => {
    // Récupérer le jeton d'accès depuis le stockage local
    const token = localStorage.getItem("token");
    console.log("affichage du token");
    console.log(token);

    try {
      const response = await axios.get('http://localhost:8000/api/user_profil/', {
        headers: {
          'Authorization':`Token${token}`
        }
      });
      setUser(response.data);
      console.log("affichage des data");
      console.log(response.data);
      
    } catch (error) {
      console.error("Erreur lors de la récupération des données de l'utilisateur :", error);
    }
  }

  useEffect(() => {
    UserData();
  }, []);

  return (
    <div>
      <h1 className="pageM">{user.email}</h1>
    </div>
  );
};

export default MemberPage;
