import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router";
import { Button } from "@mui/material";
import CityMap from "./CityMap";
import ChannelsPage from "./ChannelsPage";
import ProfilPage from "./ProfilPage";
import AddCityPage from "./AddCityPage";

const MemberPage = () => {
  const [user, setUser] = useState({});
  const navigate = useNavigate();
  const [page, setPage] = useState("chat");
  const [inRoom, setInRoom] = useState(false);
  const [sub, setSub] = useState([]);
  const [ws, setWs] = useState(null);
  const [ListeWs, setListeWs] = useState([]);
  const [room_name, setRoom_name] = useState("");
  const [room_surname, setRoom_surname] = useState("");
  const [init,setInit]= useState(false)

  const getSub2 = async () => {
    const token = localStorage.getItem("token");
    try {
      const response = await axios.get(`http://localhost:8000/api/get_sub`, {
        headers: {
          Authorization: `Token ${token}`,
        },
      });
      setSub(response.data);
      const existingEndpoints = ListeWs.map((ws) => ws.url);
      sub.forEach((s) => {
        const endpoint = `ws://localhost:8000/ws/chat/${s.city_id}/`;
        if (!existingEndpoints.includes(endpoint)) {
          setListeWs((prevWS) => [...prevWS, new WebSocket(endpoint)]);
        }
      });
    } catch {
      alert("Erreur du serveur...");
    }
  };

  useEffect(() => {
    if (init===false){
    const getSub = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get(`http://localhost:8000/api/get_sub`, {
          headers: {
            Authorization: `Token ${token}`,
          },
        });
        setSub(response.data);
        const existingEndpoints = ListeWs.map((ws) => ws.url);
        sub.forEach((s) => {
          const endpoint = `ws://localhost:8000/ws/chat/${s.city_id}/`;
          if (!existingEndpoints.includes(endpoint)) {
            setListeWs((prevWS) => [...prevWS, new WebSocket(endpoint)]);
          }
        });
      } catch {
        alert("Erreur du serveur...");
      }
    };
    getSub();
    setInit(true)
    }
  }, [sub,ListeWs,init]);

  const handleChannel = () => {
    setPage("chat");
  };

  const handleMap = () => {
    setPage("city_map");
  };

  const handleAdd = () => {
    setPage("add");
  };

  const handleProfil = () => {
    setPage("profil");
  };

  const handleLogout = () => {
    alert("Vous vous êtes déconnecté de : " + user.email);
    try {
      ws.close();
    } catch {}
    localStorage.removeItem("token");
    navigate("/signin");
  };

  useEffect(() => {
    // Fonction asynchrone pour charger les données de l'utilisateur
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get(
          "http://localhost:8000/api/user_profil/",
          {
            headers: {
              Authorization: `Token ${token}`,
            },
          }
        );
        setUser(response.data);
      } catch (error) {
        console.error(
          "Erreur lors de la récupération des données de l'utilisateur :",
          error
        );
        navigate("/signin");
      }
    };

    fetchData(); // Appeler la fonction fetchData lorsque le composant est monté.
  }, [navigate]); // Assurez-vous que fetchData soit appelée uniquement lorsque navigate change.

  const GoToRoom = (index, room_surname) => {
    setWs(ListeWs[index]);
    setRoom_name(room_name);
    setRoom_surname(room_surname);
    setInRoom(true);
  };

  return (
    <div className="background-member-page">
      <div className="nav-bar">
        <div className="cityty-style-member-page">CITYTY</div>
        <div className="user-nav-bar">{user.email}</div>
      </div>
      <div className="nav-bar2">
        <div className="button-box-nav-bar">
          <Button
            variant="contained"
            className="button-nav-bar"
            onClick={() => handleChannel()}
          >
            Channels
          </Button>
        </div>
        <div className="button-box-nav-bar">
          <Button
            variant="contained"
            className="button-nav-bar"
            onClick={() => handleMap()}
          >
            Map
          </Button>
        </div>
        <div className="button-box-nav-bar">
          <Button
            variant="contained"
            className="button-nav-bar"
            onClick={() => handleProfil()}
          >
            Profil
          </Button>
        </div>
        <div className="button-box-nav-bar">
          <Button
            variant="contained"
            className="button-nav-bar"
            onClick={() => handleAdd()}
          >
            Add
          </Button>
        </div>
        <div className="button-box-nav-bar">
          <Button
            variant="contained"
            color="error"
            className="button-nav-bar"
            onClick={() => handleLogout()}
          >
            LogOut
          </Button>
        </div>
      </div>

      {page === "city_map" ? <CityMap getSub={getSub2} /> : <div></div>}

      {page === "chat" ? (
        <div>
          {inRoom ? (
            <ChannelsPage
              user={user}
              ws={ws}
              setInRoom={setInRoom}
              room_name={room_name}
              room_surname={room_surname}
              getSub={getSub2}
            />
          ) : (
            <div>
              <h1>Page des chats</h1>
              <div className="roomButtonBox">
                {sub.map((s, index) => (
                  <div className="roomButton">
                    <Button
                      variant="contained"
                      margin="normal"
                      onClick={() => GoToRoom(index, s.surname)}
                      color="secondary"
                      key={index}
                    >
                      {s.surname} , {s.ville}
                    </Button>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      ) : (
        <div></div>
      )}

      {page === "profil" ? (
        <ProfilPage user={user} setUser={setUser} />
      ) : (
        <div></div>
      )}

      {page === "add" ? <AddCityPage user={user} /> : <div></div>}
    </div>
  );
};

export default MemberPage;
