import Form from "react-bootstrap/Form";
import React from "react";
import Button from "react-bootstrap/Button";
import { Card } from "react-bootstrap";
import { motion } from "framer-motion";
import { useNavigate } from "react-router";

const MailConfirm = () => {
  const navigate = useNavigate();

  const GoToSignUp = () => {
    navigate("/signup");
  };


  return (
    <motion.div
            className="background_signin"
            initial={{ backgroundColor: ["#33ff55"] }}
            animate={{ backgroundColor: ["#33ff55", "#efaf1a", "#4d72f1"] }}
            transition={{
              duration: 20,
              ease: "linear",
              repeat: Infinity,
              repeatType: "reverse",
            }}
          >
    <motion.div
      initial={{ y: "5vh" }}
      animate={{ y: 0 }}
      transition={{ duration: 1, type: "spring", stiffness: 100 }}
    >
      <Card border="success" style={{ width: "18rem" }} className="card_signin">
        <Card.Header className="cityty_style">Cityty</Card.Header>
        <Card.Body>
          <Form>
            <Form.Group className="mb-3" controlId="formGroupCode">
              <Form.Control type="code" placeholder="Enter confirmation code" />
            </Form.Group>
          </Form>
          <div className="container-button_form">
            <Button variant="link" className="button_form">
              Check
            </Button>
            <Button
              variant="link"
              className="button_form"
              onClick={() => GoToSignUp()}
            >
              Back
            </Button>
          </div>
        </Card.Body>
      </Card>
    </motion.div>
    </motion.div>
  );
};

export default MailConfirm;
