import Form from "react-bootstrap/Form";
import React from "react";
import Button from "react-bootstrap/Button";
import { useNavigate } from "react-router";
import { Card } from "react-bootstrap";
import { motion } from "framer-motion";

const SignUp = () => {
  const navigate = useNavigate();

  const GoToSignIn = () => {
    navigate("/");
  };

  const GoToConfirmMail = () => {
    navigate("/mailconfirm");
  };

  return (
    <motion.div
      className="background_signin"
      initial={{ backgroundColor: ["#33ff55"] }}
      animate={{ backgroundColor: ["#33ff55", "#efaf1a", "#4d72f1"] }}
      transition={{
        duration: 20,
        ease: "linear",
        repeat: Infinity,
        repeatType: "reverse",
      }}
    >
      <motion.div
        initial={{ y: "5vh" }}
        animate={{ y: 0 }}
        transition={{ duration: 1, type: "spring", stiffness: 100 }}
      >
        <Card
          border="success"
          style={{ width: "18rem" }}
          className="card_signin"
        >
          <Card.Header className="cityty_style">Cityty</Card.Header>
          <Card.Body>
            <Form>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control type="email" placeholder="Enter email" />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Control type="password" placeholder="Password" />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Control
                  type="password"
                  placeholder="Confirm your password"
                />
              </Form.Group>
            </Form>
            <div className="container-button_form">
              <Button
                variant="link"
                className="button_form"
                onClick={() => GoToConfirmMail()}
              >
                Submit
              </Button>
              <Button
                variant="link"
                className="button_form"
                onClick={() => GoToSignIn()}
              >
                Back
              </Button>
            </div>
          </Card.Body>
        </Card>
      </motion.div>
    </motion.div>
  );
};

export default SignUp;
