import Form from "react-bootstrap/Form";
import React from "react";
import Button from "react-bootstrap/Button";
import { useNavigate } from "react-router";
import { Card } from "react-bootstrap";
import { motion, useAnimation } from "framer-motion";

const SignUp = () => {

  const navigate = useNavigate();
  const controls = useAnimation();

  const ballVariants = {
    initial: { x: -100, y: 100 }, // Position initiale de la balle
    animate: {
      x: 100, // Déplacement horizontal
      y: -100, // Déplacement vertical
      transition: {
        duration: 1, // Durée de l'animation
        ease: "easeOut", // Courbe d'animation
        repeat: Infinity, // Répète l'animation indéfiniment
        repeatType: "reverse", // Inverser la direction à chaque répétition
      },
    },
  };

  const GoToSignIn = () => {
    navigate("/");
  };

  return (
    <div className="background_signin">
      <motion.div
        className="ball"
        variants={ballVariants}
        initial="initial"
        animate="animate"
        onAnimationComplete={() => controls.start("animate")}
      />
        <motion.div
          initial={{ y: "5vh" }}
          animate={{ y: 0 }}
          transition={{ duration: 1, type: "spring", stiffness: 100 }}
        >
          <Card
            border="success"
            style={{ width: "18rem" }}
            className="card_signin"
          >
            <Card.Header className="cityty_style">Cityty</Card.Header>
            <Card.Body>
              <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Control type="email" placeholder="Enter email" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Control type="password" placeholder="Password" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Control
                    type="password"
                    placeholder="Confirm your password"
                  />
                </Form.Group>
              </Form>
              <div className="container-button_form">
                <Button variant="link" className="button_form">
                  Submit
                </Button>
                <Button
                  variant="link"
                  className="button_form"
                  onClick={() => GoToSignIn()}
                >
                  Back
                </Button>
              </div>
            </Card.Body>
          </Card>
        </motion.div>
    </div>
  );
};

export default SignUp;
