import { Button, TextField } from "@mui/material";
import React, { useState, useEffect } from "react";

const ChannelsPage = () => {
  const [messagesR, setMessagesR] = useState([]);
  const [message, setMessage] = useState("");
  const roomName = "salle"; // Remplacez par le nom de votre salle
  const user = "Thomas";
  //const chatSocket = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

  const ws = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

  ws.onopen = () => console.log("ws opened");

  ws.onclose = () => console.log("ws closed");

  useEffect(() => {
    // Fonction qui sera appelée à chaque mise à jour de messages
    const handleNewMessage = (e) => {
      const data = JSON.parse(e.data);
      setMessagesR((prevMessages) => {
        if (!prevMessages.some((msg) => msg.message === data.message)) {
          return [...prevMessages, data];
        }
        return prevMessages;
      });
    };

    // Écoutez les messages lorsque le composant est monté
    ws.onmessage = handleNewMessage;

    // Nettoyez l'écouteur lorsque le composant est démonté
    return () => {
      ws.onmessage = null;
    };
  }, []);

  const SendMessage = () => {
    ws.send(
      JSON.stringify({
        message: message,
        user: user,
      })
    );
  };

  return (
    <div>
      <h1>Page des chats</h1>
      <TextField
        id="message"
        label="Enter message"
        size="normal"
        variant="filled"
        margin="normal"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button
        variant="contained"
        margin="normal"
        onClick={() => SendMessage()}
        color="success"
      >
        Send Message
      </Button>
      {messagesR.map((m, index) => (
        <h1 key={index}>{m}</h1>
      ))}
    </div>
  );
};

export default ChannelsPage;
