import { Button, TextField } from "@mui/material";
import React, { useState, useEffect } from "react";
import axios from "axios";

const ChannelsPage = ({ user, ws, setInRoom }) => {
  const [DatasList, setDatasList] = useState([]);
  const [message, setMessage] = useState("");

  const getMsg = async () => {
    const token = localStorage.getItem("token");
    try {
      const response = await axios.get(
        `http://localhost:8000/api/city_map/?departement=${selectedDepartment.numero}`,
        {
          headers: {
            Authorization: `Token ${token}`,
          },
        }
      );
      setDatasList(response.data);
    } catch {
      alert("Erreur du serveur...");
      setInRoom(false);
    }
  };

  useEffect(()=>{
    getMsg();
  },[])

  ws.onopen = () => console.log("ws opened");
  ws.onmessage = (e) => {
    const data = JSON.parse(e.data);
    setDatasList((prevDatasList) => [...prevDatasList, data]);
  };
  ws.onclose = () => console.log("ws closed");
  const SendMessage = () => {
    ws.send(
      JSON.stringify({
        message: message,
        user: user.email,
      })
    );
    setMessage("");
    setInRoom(false);
  };

  return (
    <div>
      <h1>Page des chats</h1>
      <TextField
        id="message"
        label="Enter message"
        size="normal"
        variant="filled"
        margin="normal"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button
        variant="contained"
        margin="normal"
        onClick={() => SendMessage()}
        color="success"
      >
        Send Message
      </Button>
      {DatasList.map((d, index) => (
        <h1 key={index}>
          {d.message},{d.user}
        </h1>
      ))}
    </div>
  );
};

export default ChannelsPage;
