import { Button, TextField } from "@mui/material";
import React, { useState, useEffect, useRef, useLayoutEffect } from "react";
import axios from "axios";

const ChannelsPage = ({ user, ws, setInRoom, room_name }) => {
  const [DatasList, setDatasList] = useState([]);
  const [message, setMessage] = useState("");
  const convBoxRef = useRef(null);

  useLayoutEffect(() => {
    const { current } = convBoxRef;
    if (current) {
      current.scrollTop = current.scrollHeight;
    }
  }, []);

  useEffect(() => {
    const getMsg = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get(
          `http://localhost:8000/api/get_msg/?room_name=${room_name}`,
          {
            headers: {
              Authorization: `Token ${token}`,
            },
          }
        );
        console.log("message precedents:", response.data);
        setDatasList(response.data);
      } catch {
        alert("Erreur du serveur...");
      }
    };
    getMsg();
  }, [room_name]);

  ws.onopen = () => console.log("ws opened");
  ws.onmessage = (e) => {
    const data = JSON.parse(e.data);
    setDatasList((prevDatasList) => [...prevDatasList, data]);
  };
  ws.onclose = () => console.log("ws closed");
  const SendMessage = () => {
    ws.send(
      JSON.stringify({
        message: message,
        user: user.email,
      })
    );
    setMessage("");
  };

  const ExitFromRoom = () => {
    setInRoom(false);
  };

  return (
    <div className="room">
      <div className="convBox" ref={convBoxRef}>
        {DatasList.map((d, index) => (
          <div key={index}>
            {user.email === d.user ? (
              <div className="bulleUser">
                <text className="textBulle">{d.message}</text>
              </div>
            ) : (
              <div className="bulle">
                <text className="textBulle">{d.message}</text>
              </div>
            )}
          </div>
        ))}
      </div>
      <TextField
        id="message"
        label="Enter message"
        size="normal"
        variant="filled"
        margin="normal"
        multiline
        maxRows={10}
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button
        variant="contained"
        margin="normal"
        onClick={() => SendMessage()}
        color="success"
      >
        Envoyer un message
      </Button>
      <Button
        variant="contained"
        margin="normal"
        onClick={() => ExitFromRoom()}
        color="error"
      >
        Retour
      </Button>
    </div>
  );
};

export default ChannelsPage;
