import { Button, TextField } from "@mui/material";
import React, { useState, useEffect } from "react";

const ChannelsPage = () => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const roomName = "salle"; // Remplacez par le nom de votre salle
  const user = "Thomas";
  const chatSocket = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

  useEffect(() => {
    let ws = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);
    ws.onopen = () => console.log('ws opened');
    ws.onclose = () => console.log('ws closed');

    ws.onmessage = e => {
      const message = JSON.parse(e.data);
      console.log('e', message);
    };

    return () => {
      ws.close();
    }
  }, []);

  const SendMessage = () => {
    chatSocket.send(JSON.stringify({ message: message, user: user }));
  };

  return (
    <div>
      <h1>Page des chats</h1>
      {messages.map((message) => (
        <div>{message.message}</div>
      ))}
      <TextField
        id="message"
        label="Enter message"
        size="normal"
        variant="filled"
        margin="normal"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button
        variant="contained"
        margin="normal"
        onClick={() => SendMessage()}
        color="success"
      >
        Send Message
      </Button>
    </div>
  );
};

export default ChannelsPage;
