import { Button, TextField } from "@mui/material";
import React, { useState, useEffect } from "react";

const ChannelsPage = () => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const roomName = "salle"; // Remplacez par le nom de votre salle

  useEffect(() => {
    // Ouvrir la connexion WebSocket lorsque le composant est monté
    const chatSocket = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

    chatSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setMessages((prevMessages) => [...prevMessages, data]);
    };

    return () => {
      // Fermer la connexion WebSocket lorsque le composant est démonté
      chatSocket.close();
    };
  }, []);

  const SendMessage = () => {
    chatSocket.send(JSON.stringify({ message: message }));
  };

  return (
    <div>
      <h1>Page des chats</h1>
      {messages.map((message) => (
        <div>{message.message}</div>
      ))}
      <TextField
        id="message"
        label="Enter message"
        size="normal"
        variant="filled"
        margin="normal"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button
        variant="contained"
        margin="normal"
        onClick={() => SendMessage()}
        color="success"
      >
        Send Message
      </Button>
    </div>
  );
};

export default ChannelsPage;
