import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router";
import { Button } from "@mui/material";

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import 'leaflet/dist/leaflet.css'; // Importez les styles CSS de Leaflet ici

const MemberPage = () => {
  const [user, setUser] = useState({});
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/signin");
  };

  useEffect(() => {
    // Fonction asynchrone pour charger les données de l'utilisateur
    const fetchData = async () => {
      // Récupérer le jeton d'accès depuis le stockage local
      const token = localStorage.getItem("token");

      try {
        const response = await axios.get(
          "http://localhost:8000/api/user_profil/",
          {
            headers: {
              Authorization: `Token ${token}`,
            },
          }
        );
        setUser(response.data);
      } catch (error) {
        console.error(
          "Erreur lors de la récupération des données de l'utilisateur :",
          error
        );
        navigate("/signin");
      }
    };

    fetchData(); // Appeler la fonction fetchData lorsque le composant est monté.
  }, [navigate]); // Assurez-vous que fetchData soit appelée uniquement lorsque navigate change.

  return (
    <div>
      <div className="nav-bar">
        <div className="cityty-style-member-page">CITYTY</div>
        <div className="user-nav-bar">{user.email}</div>
      </div>
      <div className="nav-bar2">
        {/* Vos boutons de navigation */}
      </div>
      <div className="background-member-page">
        <div className="map-box">
          <MapContainer
            center={[51.505, -0.09]} // Remplacez ces coordonnées par celles de votre choix
            zoom={13}
            scrollWheelZoom={false}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker position={[51.505, -0.09]}> // Remplacez ces coordonnées par celles de votre choix
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

export default MemberPage;
